SELECT 
	string_agg(nfo.id_ocorrencia_nf::text, ',') 
	--nf.chave_nfe,
	--nfl.*
FROM 
	scr_notas_fiscais_imp nf
	LEFT JOIN scr_notas_fiscais_imp_log_atividades nfl
		ON nf.id_nota_fiscal_imp = nfl.id_nota_fiscal_imp
	LEFT JOIN scr_notas_fiscais_imp_ocorrencias nfo
		ON nfo.id_nota_fiscal_imp = nfl.id_nota_fiscal_imp
WHERE
	remetente_id = 6
	AND nf.data_expedicao >= '2021-09-20'
	AND nfl.atividade_executada = 'OCORREN. ENVIADA P/COMPROVEI SEM SUCESSO'
	AND nfo.id_ocorrencia = 1
	
ORDER BY 
	nfo.data_ocorrencia DESC



SELECT 
	
	nf.chave_nfe,
	nf.obs,
	nfl.*
FROM 
	scr_notas_fiscais_imp nf
